import React, { useState } from 'react'
import ReviewForm from './ReviewForm'

function CreateForm() {

  const [name, updateName] = useState();
  const [email, updateEmail] = useState();

  const [venue, updateVenue] = useState();
  const [dj, updateDj] = useState();
  const [photog, updatePhotog] = useState();
  const [florist, updateFlorist] = useState();

  const inputHandler = (e, action) => {
    e.preventDefault();
    action(e.target.value);
  }

  return (
    <div>
      <form>
        <div className='name-email-fields'>
          <label htmlFor='name'>Name:<input type='text' id='name' onChange={inputHandler(updateName)} /></label>
          <label htmlFor='email'>Email:<input type='text' id='email' onChange={inputHandler(updateEmail)} /></label>
        </div>
        <div className='vedor-fields'>
          <ReviewForm field='Venue' action={updateVenue} />
          <ReviewForm field='DJ' action={updateDj} />
          <ReviewForm field='Photography' action={updatePhotog} />
          <ReviewForm field='Florist' action={updateFlorist} />
        </div>
        <input type='submit' value='Post My Reviews'/>
      </form>
    </div>
  )
}

export default CreateForm
